function mk_cut_words(title,size){if(title.length>size){return title=title.substring(0,size)+'...';}else{return title;}}
function mk_diff_format(num){if(num>=1000){return'<div class="pv-box-100 kformat">+'+(num/1000).toFixed(1).replace(/\.0$/,'')+'k</div>';}else{return'<div class="pv-box-100 kformat">+'+(num).toFixed(0).replace(/\.0$/,'')+'</div>';}};